import React from 'react';

import useSpeechToText from './Hooks';

import micIcon from './mic.svg';

import './App.css';

export default function App() {
  const {
    error,
    interimResult,
    isRecording,
    results,
    startSpeechToText,
    stopSpeechToText
  } = useSpeechToText({
    continuous: true,
    crossBrowser: true,
    googleApiKey: 'AIzaSyBAwyfhgmzXyVDhaWYAH5c42FJfPe6IN50',
    //process.env.REACT_APP_API_KEY,
    speechRecognitionProperties: { interimResults: true },
    timeout: 30000
  });

  var doNotRe = [];
  var num =1;

  for (let index = 0; index < results.length; index++) {
    for (let j = 0; index < doNotRe.length; j++) {
      if ( results[index] != doNotRe[j] ) {
        doNotRe[num++] = results[index];
      } 
    }
  }


  if (error) {
    return (
      <div
        style={{
          maxWidth: '600px',
          margin: '100px auto',
          textAlign: 'center'
        }}
      >
        <p>
          {error}
          <span style={{ fontSize: '3rem' }}>ü§∑‚Äç</span>
        </p>
      </div>
    );
  }



  return (
    <div
      style={{
        maxWidth: '600px',
        margin: '100px auto',
        textAlign: 'center'
      }}
    >
      <h3> ÿ±ÿ≥ÿßŸÑÿ© ÿßŸÑŸÖÿ∫Ÿäÿ® ŸÑŸÉ : {isRecording.toString() == 'false'? 'ÿßŸÇÿ±ÿ£ ÿ®ÿπÿØ ÿ±ÿ§Ÿäÿ™ŸÉ ÿßŸÑÿ¢ŸäŸá ÿπŸÑŸâ ÿßŸÑÿ¥ÿßÿ¥ÿ©':'ÿßŸÜÿß ÿßÿ≥ÿ™ŸÖÿπ ÿßŸÑŸäŸÉ ÿßŸÑÿßŸÜ'}</h3>
      <button onClick={isRecording ? stopSpeechToText : startSpeechToText}>
        <span>{isRecording ? 'ÿ™ŸàŸÇŸÅ ÿπŸÜ ÿßŸÑÿ™ÿ∫ŸäŸäÿ®' : 'ÿßÿ®ÿØÿ£ ÿßŸÑÿ™ÿ∫ŸäŸäÿ®'}</span>
        <img data-recording={isRecording} src={micIcon} alt="" />
      </button>
      <ul>
        {results.map((result, index) => (
          <li key={index}>{result}</li>

        ))}

      {results.map((result, index) => (
          console.log(results[index])


        ))}
        {interimResult && <li>{interimResult}</li>}

      </ul>
    </div>
  );
}
